package com.at.wangxu.leetcode;

/**
 * 214. 最短回文串
 * <p>
 * 给定一个字符串 s，你可以通过在字符串前面添加字符将其转换为回文串。找到并返回可以用这种方式转换的最短回文串。
 * <p>
 * 示例 1:
 * <p>
 * 输入: "aacecaaa"
 * 输出: "aaacecaaa"
 * 示例 2:
 * <p>
 * 输入: "abcd"
 * 输出: "dcbabcd"
 */
public class ShortestPalindrome {

    String string = "";

    public String shortestPalindrome(String s) {
        if (s.length() == 0)
            return "";
        for (int i = s.length() / 2; i >= 0 && string.length() == 0; i--) {
            centeredOn(i, i, s);
            if (i + 1 < s.length() && s.charAt(i) == s.charAt(i + 1))
                centeredOn(i, i + 1, s);
        }
        return string;
    }

    /**
     * 以i,j为中心的回文串
     *
     * @param i i
     * @param j j
     * @param s s
     */
    public void centeredOn(int i, int j, String s) {
        while (i >= 0) {
            if (j >= s.length() || s.charAt(i) != s.charAt(j)) {
                return;
            } else {
                i--;
                j++;
            }
        }
        StringBuilder substring = new StringBuilder(s.substring(j));
        string = substring.reverse().toString() + s;
    }

    public static void main(String[] args) {
        ShortestPalindrome shortestPalindrome = new ShortestPalindrome();
        System.out.println(shortestPalindrome.shortestPalindrome("aacecaaa"));
//        System.out.println(shortestPalindrome.shortestPalindrome(""));
    }
}
